async function _doCall(a,b){try{const c=await fetch(a,b);if(c.ok){const a=await c.json();return Promise.resolve(a)}return Promise.reject(c)}catch(a){return a}}export default{get:(a,b={})=>_doCall(a,b),head:(a,b={})=>_doCall(a,{method:"HEAD",...b}),post:(a,b,c={})=>_doCall(a,{method:"POST",body:JSON.stringify(b),headers:{"Content-Type":"application/json"},...c}),put:(a,b,c={})=>_doCall(a,{method:"PUT",body:JSON.stringify(b),headers:{"Content-Type":"application/json"},...c}),delete:(a,b={})=>_doCall(a,{method:"DELETE",...b}),options:(a,b={})=>_doCall(a,{method:"OPTIONS",...b})};
